import { product as phones } from 'api/mockPhones'
import { categories } from 'api/mockCategories'
import categoriesType from 'api/mockType'
import * as R from 'ramda'

export const fetchPhones = async (nameType) => {
     return new Promise(resolve => {
        const loadProduct = R.prop(nameType.toLowerCase(), phones)
        resolve(loadProduct)
    })
}

export const loadMorePhones = async ({ offset }) => {
    return new Promise(resolve => {
        resolve(phones)
    })
}

export const fetchPhoneById = async (id, nameType) => {
    return new Promise(resolve => {
        const loadProduct = R.prop(nameType.toLowerCase(), phones)
        const phone = R.find(R.propEq('id', id), loadProduct)       
        resolve(phone)
    })
}
export const fetchCategories = async (nameType) => {
    return new Promise(resolve => {
        const loadProduct = R.prop(nameType.toLowerCase(), categories)
        resolve(loadProduct)        
    })
}
export const fetchCategoriesType = async () => {
    return new Promise(resolve => {
        resolve(categoriesType)
    })
}