import React from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

import { getTotalBasketCount, getTotalBasketPrice, getActiveCategoryTypeId } from 'selectors'
const BasketCart = ({ totalBasketCount, totalPrice, activeCategoryTypeId }) => {
    return (
        <div className='cart'><div className='dropdown'>
            <Link
                to={`/basket`}
                id='dLabel'
                className='btn btn-inverse btn-block btn-lg'
            >
                <i className='fa fa-fa-shopping-cart'> </i>
                <span > {totalBasketCount} item(s) - $ {totalPrice}</span>
            </Link></div></div>
    )
}
const mapStateToProps = (state, ownProps) => {
    return {
        totalBasketCount: getTotalBasketCount(state),
        totalPrice: getTotalBasketPrice(state),
        activeCategoryTypeId: getActiveCategoryTypeId(ownProps)
    }
}
export default connect(mapStateToProps, null)(BasketCart)