import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { withRouter } from 'react-router'
import { compose } from 'redux'
import classNames from 'classnames'

import * as R from 'ramda'
import { getCategoriesType } from 'selectors'
import { fetchPhones, fetchCategories, CategoryType } from 'actions'

class CategoriesType extends Component {

    render() {
        const { categories, CategoryType, categoryProg } = this.props
        const getActiveState = R.propEq('name', categoryProg)

        const renderCategory = (category, index) => {
            const linkClass = classNames({
                'list-group-item': true,
                'active': getActiveState(category)
            })
            return (
                <Link to={`/categories/${category.name}`} className={linkClass} key={index}
                    onClick={() => {
                        CategoryType(category.name)
                        this.props.fetchPhones(category.name)
                        this.props.fetchCategories(category.name)
                    }} > {category.name}</Link>
            )
        }
        return (
            <div className='well'>
                <h4>Ware</h4>
                <div className='list-group'>
                    {categories.map((category, index) => renderCategory(category, index))}
                </div>
            </div>
        )
    }
}
const mapStateToProps = (state) => ({
    categories: getCategoriesType(state),
    categoryProg: state.categoryPage.name
})

const mapDispatchToProps = {
    fetchPhones,
    fetchCategories,
    CategoryType
}
export default compose(
    withRouter,
    connect(mapStateToProps, mapDispatchToProps)
)(CategoriesType)