import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import * as R from 'ramda'

import {
    getBacketPhonesWithCount,
    getTotalBasketPrice
} from 'selectors'
import { removePhonePromBasket, cleanBasket, basketCheckout } from 'actions'
const Basket = ({ phones, totalPrice, removePhonePromBasket, cleanBasket, basketCheckout, category }) => {
    const isBaskertEmpty = R.isEmpty(phones)
    const renderContent = () => (
        <div>
            {isBaskertEmpty && <div>Your shopping cart is empty</div>}
            <div className='table-responsive'>
                <table className='table-bordered table-striped table-condensed cf'>
                    <tbody>
                        {
                            phones.map((phone, index) => (
                                <tr key={index} className='item-checout'>
                                    <td className='first-column-checkout'>
                                        <img className='img-thumbnail'
                                            src={phone.image}
                                            alt={phone.name} />
                                    </td>
                                    <td>{phone.name}</td>
                                    <td>${phone.price}</td>
                                    <td>{phone.count} шт</td>
                                    <td><span className='delete-cart'
                                        onClick={() => removePhonePromBasket(phone.id)}></span></td>
                                </tr>
                            ))
                        }
                    </tbody>
                </table>
            </div>
            {R.not(isBaskertEmpty) &&
                <div className='row'>
                    <div className='pull-right total-user-checkout'>
                        <b>
                            Total:
                        </b>
                        ${totalPrice}
                    </div>
                </div>
            }
        </div>
    )


    const renderSidebar = () => (

        <div  >
            <Link
                to={`/categories/${category}`}
                className='btn btn-info'>
                <span className='glyphicon glyphicon-info-sign'></span>
                <span className=''>Continue shoping!</span>
            </Link>
            {
                R.not(isBaskertEmpty) &&
                <div >
                    <button onClick={cleanBasket} className='btn btn-danger'>
                        <span className='glyphicon glyphicon-trash'>Clean cart </span>
                    </button>
                    <button onClick={() => basketCheckout(phones)} className='btn btn-success'>
                        <span className='glyphicon glyphicon-envelope'>Checkout </span>
                    </button>
                </div>
            }
        </div >
    )

    return (
        <div className='view-container'>
            <div className='container'>
                <div className='row'>
                    <div className='col-md-9'>
                        {renderContent()}
                    </div>
                    <div className='col-md-3 btn-user-checkout'>
                        {renderSidebar()}
                    </div>
                </div>
            </div>
        </div>
    )
}
const mapStateToProps = state => {
    return {
        phones: getBacketPhonesWithCount(state),
        totalPrice: getTotalBasketPrice(state),
        category: state.categoryPage.name
    }
}
const mapDispachToProps = {
    removePhonePromBasket,
    cleanBasket,
    basketCheckout
}
export default connect(mapStateToProps, mapDispachToProps)(Basket)
