import * as R from 'ramda'

import {
    FETSH_CATEGORIES_TYPE_SUCCESS,
} from 'actionTypes'

const initialState = {}

export default (state = initialState, { type, payload }) => {
    switch (type) {
        case FETSH_CATEGORIES_TYPE_SUCCESS:
            const newValue = R.indexBy(R.prop('name'), payload)
            return R.merge(state, newValue)
        default: return state
    }

}
