import { combineReducers } from 'redux'
import { connectRouter } from 'connected-react-router'
import phones from 'reducers/phones'
import phonesPage from 'reducers/phonesPage'
import phonePage from 'reducers/phonePage'
import basket from 'reducers/basket'
import categories from 'reducers/categories'
import categoriesType from 'reducers/categoriesType'
import categoryPage from 'reducers/categoryPage'
export default (history) =>
  combineReducers({
    phones,
    phonesPage,
    phonePage,
    basket,
    categories,
    categoriesType,
    categoryPage,
    router: connectRouter(history),
  })
