import { FETSH_PHONE_BY_ID_SUCCESS } from 'actionTypes'
import * as R from 'ramda'

const initialState = {
    id: null
}
export default (state = initialState, { type, payload }) => {
    switch (type) {
        case FETSH_PHONE_BY_ID_SUCCESS:
            return R.merge(state, { id: R.prop('id', payload) })
        default:
            return state
    }
}