import * as R from 'ramda'



import {
  FETSH_PHONE_SUCCESS,
  LOAD_MORE_PHONE_SUCCESS,
  FETSH_PHONE_BY_ID_SUCCESS
} from 'actionTypes'

const initialState = {}

export default (state = initialState, { type, payload }) => {

  switch (type) {
    case FETSH_PHONE_SUCCESS:
      const newValue = R.indexBy(R.prop('id'), payload)     
      return R.merge(state, newValue)
    case LOAD_MORE_PHONE_SUCCESS:
      const moreValue = R.indexBy(R.prop('id'), payload)
      return R.merge(state, moreValue)
    case FETSH_PHONE_BY_ID_SUCCESS:
      return R.assoc(payload.id, payload, state)
    default: return state
  }

}

