import { FETSH_PHONE_SUCCESS, LOAD_MORE_PHONE_SUCCESS, SEARCH_PHONE } from 'actionTypes'
import * as R from 'ramda'

const initialState = {
    ids: [],
    search: ''
}
export default (state = initialState, { type, payload }) => {
    switch (type) {
        case FETSH_PHONE_SUCCESS:            
            return R.merge(state, { ids: R.pluck('id', payload) }) // записуємо в стате 
        case LOAD_MORE_PHONE_SUCCESS:
            const ids = R.pluck('id', payload)
            return R.merge(state, { ids: R.concat(state.ids, ids) })
        case SEARCH_PHONE:
            return R.merge(state, { search: payload })
        default:
            return state
    }
}